// ProfileTab.jsx
import React, { useState, useEffect } from 'react';

const ProfileTab = () => {
    const [userData, setUserData] = useState({
        id: null,
        username: '',
        rating: 0,
        lastActivity: null
    });

    useEffect(() => {
        // Здесь мы будем получать данные пользователя из Max
        loadUserData();
    }, []);

    const loadUserData = async () => {
        try {
            // Получаем данные пользователя через Max Bridge API
            const maxUser = await window.max.user.get();

            // Теперь получаем данные из нашей базы
            const response = await fetch(`${process.env.REACT_APP_URL}/users/${maxUser.id}`);

            if (response.ok) {
                const userFromDB = await response.json();
                setUserData(userFromDB);
            } else {
                // Если пользователя нет в базе, создаем нового
                createNewUser(maxUser);
            }
        } catch (error) {
            console.error('Ошибка загрузки данных пользователя:', error);
        }
    };

    const createNewUser = async (maxUser) => {
        try {
            const newUser = {
                id: maxUser.id,
                username: maxUser.name || maxUser.username || 'Пользователь Max',
                rating: 0,
                lastActivity: new Date().toISOString()
            };

            const response = await fetch(`${process.env.REACT_APP_URL}/users`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newUser),
            });

            if (response.ok) {
                setUserData(newUser);
            }
        } catch (error) {
            console.error('Ошибка создания пользователя:', error);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Профиль</h2>

            <div style={{
                background: '#f5f5f5',
                padding: '20px',
                borderRadius: '8px',
                marginBottom: '20px'
            }}>
                <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>
                    {userData.username}
                </div>

                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                    <span>Рейтинг:</span>
                    <span style={{
                        fontSize: '20px',
                        fontWeight: 'bold',
                        color: '#2e7d32'
                    }}>
            {userData.rating} ★
          </span>
                </div>

                {userData.lastActivity && (
                    <div style={{ marginTop: '10px', fontSize: '14px', color: '#666' }}>
                        Последняя активность: {new Date(userData.lastActivity).toLocaleDateString('ru-RU')}
                    </div>
                )}
            </div>

            <div style={{ marginTop: '20px' }}>
                <h3>Статистика</h3>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                    <div style={{ background: '#e3f2fd', padding: '15px', borderRadius: '8px' }}>
                        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>0</div>
                        <div>Событий создано</div>
                    </div>
                    <div style={{ background: '#e8f5e8', padding: '15px', borderRadius: '8px' }}>
                        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>0</div>
                        <div>Участий в событиях</div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProfileTab;
