// ProfileTab.jsx
import React, { useState, useEffect } from 'react';
import { Panel, Typography, Grid } from '@maxhub/max-ui';

const ProfileTab = () => {
    const [userData, setUserData] = useState({
        username: 'Пользователь',
        rating: 0,
        lastActivity: null
    });
    const [stats, setStats] = useState({
        eventsCount: 0,
        postsCount: 0,
        followersCount: 0,
        followingCount: 0
    });
    const [loading, setLoading] = useState(true);

    const BASE_URL = process.env.REACT_APP_URL || 'https://ecology-app-test.vercel.app';

    useEffect(() => {
        loadUserData();
    }, []);

    const loadUserData = async () => {
        try {
            // Для тестирования используем фиксированный ID
            // В реальном приложении здесь будет window.max.user.get()
            const testUserId = 1;

            // Пробуем получить данные пользователя
            const userResponse = await fetch(`${BASE_URL}/user?id=${testUserId}`);

            if (userResponse.ok) {
                const user = await userResponse.json();
                setUserData({
                    username: user.username,
                    rating: user.rating,
                    lastActivity: user.lastActivity
                });

                // Загружаем статистику
                await loadUserStats(testUserId);
            } else {
                // Если пользователь не найден, создаем тестовые данные
                console.log('Пользователь не найден, используем тестовые данные');
                setUserData({
                    username: 'Тестовый пользователь',
                    rating: 45,
                    lastActivity: new Date().toISOString()
                });
                setStats({
                    eventsCount: 3,
                    postsCount: 2,
                    followersCount: 12,
                    followingCount: 8
                });
            }
        } catch (error) {
            console.error('Ошибка загрузки данных пользователя:', error);
            // Используем тестовые данные при ошибке
            setUserData({
                username: 'Пользователь',
                rating: 0,
                lastActivity: new Date().toISOString()
            });
        } finally {
            setLoading(false);
        }
    };

    const loadUserStats = async (userId) => {
        try {
            // Здесь можно добавить запросы для получения статистики
            // Пока используем тестовые данные
            setStats({
                eventsCount: 5,
                postsCount: 3,
                followersCount: 15,
                followingCount: 10
            });
        } catch (error) {
            console.error('Ошибка загрузки статистики:', error);
        }
    };

    if (loading) {
        return (
            <div style={{ padding: '20px', textAlign: 'center' }}>
                <Typography.Body>Загрузка профиля...</Typography.Body>
            </div>
        );
    }

    return (
        <div style={{ padding: '20px' }}>
            <Typography.Display style={{ textAlign: 'center', width: '100%', marginBottom: '20px' }}>
                Профиль
            </Typography.Display>

            {/* Основная информация */}
            <Panel style={{
                background: '#f5f5f5',
                padding: '20px',
                borderRadius: '8px',
                marginBottom: '20px'
            }}>
                <Typography.Heading style={{ marginBottom: '10px' }}>
                    {userData.username}
                </Typography.Heading>

                <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '10px' }}>
                    <Typography.Body>Рейтинг:</Typography.Body>
                    <Typography.Heading style={{ color: '#2e7d32' }}>
                        {userData.rating} ★
                    </Typography.Heading>
                </div>

                {userData.lastActivity && (
                    <Typography.Caption style={{ color: '#666' }}>
                        Последняя активность: {new Date(userData.lastActivity).toLocaleDateString('ru-RU')}
                    </Typography.Caption>
                )}
            </Panel>

            {/* Статистика */}
            <Typography.Heading style={{ marginBottom: '15px' }}>Статистика</Typography.Heading>
            <Grid cols={2} gapX={10} gapY={10} style={{ marginBottom: '20px' }}>
                <Panel style={{ background: '#4f6c7c', padding: '15px', borderRadius: '8px' }}>
                    <Typography.Heading style={{ fontSize: '24px', marginBottom: '5px' }}>
                        {stats.eventsCount}
                    </Typography.Heading>
                    <Typography.Caption>Участий в событиях</Typography.Caption>
                </Panel>

                <Panel style={{ background: '#456a45', padding: '15px', borderRadius: '8px' }}>
                    <Typography.Heading style={{ fontSize: '24px', marginBottom: '5px' }}>
                        {stats.postsCount}
                    </Typography.Heading>
                    <Typography.Caption>Постов создано</Typography.Caption>
                </Panel>

                <Panel style={{ background: '#73654f', padding: '15px', borderRadius: '8px' }}>
                    <Typography.Heading style={{ fontSize: '24px', marginBottom: '5px' }}>
                        {stats.followersCount}
                    </Typography.Heading>
                    <Typography.Caption>Подписчиков</Typography.Caption>
                </Panel>

                <Panel style={{ background: '#826185', padding: '15px', borderRadius: '8px' }}>
                    <Typography.Heading style={{ fontSize: '24px', marginBottom: '5px' }}>
                        {stats.followingCount}
                    </Typography.Heading>
                    <Typography.Caption>Подписок</Typography.Caption>
                </Panel>
            </Grid>

            {/* Дополнительная информация */}
            <Panel style={{ background: '#f8f9fa', padding: '15px', borderRadius: '8px' }}>
                <Typography.Heading style={{ marginBottom: '10px' }}>О пользователе</Typography.Heading>
                <Typography.Body>
                    Активный участник экологических мероприятий. Любит природу и заботится об окружающей среде.
                </Typography.Body>
            </Panel>
        </div>
    );
};

export default ProfileTab;
