import React, { useState, useEffect } from 'react';

const ProfileTab = () => {
    const [userData, setUserData] = useState({
        user: {
            id: null,
            username: '',
            rating: 0,
            lastActivity: null
        },
        stats: {
            eventsCount: 0,
            postsCount: 0,
            followersCount: 0,
            followingCount: 0,
            totalLikes: 0
        },
        recentActivity: {
            lastEvents: [],
            lastPosts: []
        }
    });
    const [loading, setLoading] = useState(true);
    const [maxInitialized, setMaxInitialized] = useState(false);

    // Ждем инициализации Max Bridge
    useEffect(() => {
        const initializeMax = async () => {
            try {
                // Проверяем, доступен ли Max Bridge
                if (window.max && window.max.user) {
                    setMaxInitialized(true);
                } else {
                    // Если нет, ждем события инициализации
                    window.addEventListener('max-bridge-init', () => {
                        setMaxInitialized(true);
                    });

                    // Таймаут на случай, если Bridge никогда не инициализируется
                    setTimeout(() => {
                        if (window.max && window.max.user) {
                            setMaxInitialized(true);
                        } else {
                            console.warn('Max Bridge не инициализирован, используем тестовые данные');
                            setMaxInitialized(true); // Все равно продолжаем
                        }
                    }, 3000);
                }
            } catch (error) {
                console.warn('Ошибка инициализации Max Bridge:', error);
                setMaxInitialized(true); // Все равно продолжаем
            }
        };

        initializeMax();
    }, []);



    const loadUserData = async () => {
        try {
            setLoading(true);

            // Получаем данные пользователя через Max Bridge API
            const maxUser = await window.max.user.get();

            // Получаем полную статистику пользователя
            const response = await fetch(
                `${process.env.REACT_APP_URL}/user/stats?userId=${maxUser.id}`
            );

            if (response.ok) {
                const userStats = await response.json();
                setUserData(userStats);
            } else {
                // Если статистика не найдена, создаем пользователя
                await createUser(maxUser);
            }
        } catch (error) {
            console.error('Ошибка загрузки данных пользователя:', error);
        } finally {
            setLoading(false);
        }
    };

    const createUser = async (maxUser) => {
        try {
            const response = await fetch(`${process.env.REACT_APP_URL}/user`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    id: maxUser.id,
                    username: maxUser.name || maxUser.username || 'Пользователь Max'
                }),
            });

            if (response.ok) {
                // После создания загружаем статистику
                loadUserData();
            }
        } catch (error) {
            console.error('Ошибка создания пользователя:', error);
        }
    };

    if (loading) {
        return <div style={{ padding: '20px', textAlign: 'center' }}>Загрузка профиля...</div>;
    }

    return (
        <div style={{ padding: '20px' }}>
            <h2>Профиль</h2>

            {/* Основная информация */}
            <div style={{
                background: '#f5f5f5',
                padding: '20px',
                borderRadius: '8px',
                marginBottom: '20px'
            }}>
                <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>
                    {userData.user.username}
                </div>

                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                    <span>Рейтинг:</span>
                    <span style={{
                        fontSize: '20px',
                        fontWeight: 'bold',
                        color: '#2e7d32'
                    }}>
            {userData.user.rating} ★
          </span>
                </div>

                {userData.user.lastActivity && (
                    <div style={{ marginTop: '10px', fontSize: '14px', color: '#666' }}>
                        Последняя активность: {new Date(userData.user.lastActivity).toLocaleDateString('ru-RU')}
                    </div>
                )}
            </div>

            {/* Статистика */}
            <div style={{ marginTop: '20px' }}>
                <h3>Статистика</h3>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                    <div style={{ background: '#e3f2fd', padding: '15px', borderRadius: '8px' }}>
                        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{userData.stats.eventsCount}</div>
                        <div>Участий в событиях</div>
                    </div>
                    <div style={{ background: '#e8f5e8', padding: '15px', borderRadius: '8px' }}>
                        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{userData.stats.postsCount}</div>
                        <div>Постов создано</div>
                    </div>
                    <div style={{ background: '#fff3e0', padding: '15px', borderRadius: '8px' }}>
                        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{userData.stats.followersCount}</div>
                        <div>Подписчиков</div>
                    </div>
                    <div style={{ background: '#f3e5f5', padding: '15px', borderRadius: '8px' }}>
                        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{userData.stats.totalLikes}</div>
                        <div>Лайков получено</div>
                    </div>
                </div>
            </div>

            {/* Последние события */}
            {userData.recentActivity.lastEvents.length > 0 && (
                <div style={{ marginTop: '20px' }}>
                    <h3>Последние события</h3>
                    <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
                        {userData.recentActivity.lastEvents.map(event => (
                            <div key={event.id} style={{
                                background: '#f8f9fa',
                                padding: '10px',
                                borderRadius: '6px',
                                borderLeft: '4px solid #4caf50'
                            }}>
                                <div style={{ fontWeight: 'bold' }}>{event.name}</div>
                                <div style={{ fontSize: '12px', color: '#666' }}>
                                    {new Date(event.date).toLocaleDateString('ru-RU')}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProfileTab;
